    try {
        const client = await MongoClient.connect('mongodb://localhost:27017/', {
            useNewUrlParser: true
        })
        const db = client.db('nodejs-restapi');
        console.log('DB is conected');
        return db;
    } catch (e) {
        console.log(e);
    }
};

